import React, {useEffect, useState} from 'react'
import IconButton from '@mui/material/IconButton';
import Box from '@mui/material/Box';
import Dialog from '@mui/material/Dialog';
import ChildCareIcon from '@mui/icons-material/ChildCare';
import ClickAwayListener from "@mui/material/ClickAwayListener";
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemText from '@mui/material/ListItemText';
import axios from "axios";

const Friend = ({user}) => {
    return (
        <ListItem>
            <ListItemText>{user.name}</ListItemText>
        </ListItem>
    )
}

const FriendsModal = ({open, handleClose, friends, numFriends}) => {
    return (

        <Dialog open={open} sx={{padding: 5}}>
            <ClickAwayListener onClickAway={handleClose}>

                <Box sx={{p: 5}}>
                    <List>
                        {friends.map(friend => <Friend user={friend}/>)}
                    </List>
                </Box>

            </ClickAwayListener>
        </Dialog>

    )
}

const FriendsList = () => {
    const [open, setOpen] = useState(false);

    const handleClickOpen = () => {
        setOpen(true)
    }

    const handleClose = () => {
        console.log('ran')
        setOpen(false)
    }

    useEffect(() => {
        axios.get(`http://localhost:5000/api/v1/users/tasks?userId=${userId}`)
            .then(res => {
                if (res.data.tasks.length !== 0){
                    setQuests(res.data.tasks)
                    setSelectedIndex(res.data.tasks[0]._id)
                    setActiveTask(res.data.tasks[0].name)
                }
            })
    }, [])

    return(
        <Box>
            <IconButton onClick={handleClickOpen}>
                <ChildCareIcon />
            </IconButton>
            <FriendsModal open={open} handleClose={handleClose} />

        </Box>

    )
}

export default FriendsList
