import React, {useEffect, useState} from 'react'
import IconButton from '@mui/material/IconButton';
import Box from '@mui/material/Box';
import Dialog from '@mui/material/Dialog';
import ChildCareIcon from '@mui/icons-material/ChildCare';
import ClickAwayListener from "@mui/material/ClickAwayListener";
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import axios from "axios";
import TextField from "@mui/material/TextField";

const FriendsModal = ({open, handleClose, friends, numFriends, isLoading}) => {
    if (isLoading) {
        return (
            <Dialog open={open}>
                <ClickAwayListener onClickAway={handleClose}>
                    <Box sx={{p: 5}}>
                    </Box>
                </ClickAwayListener>
            </Dialog>
        )
    } else {
        return (
            <Dialog open={open}>
                <ClickAwayListener onClickAway={handleClose}>
                    <Box sx={{p: 5}}>

                    </Box>
                </ClickAwayListener>
            </Dialog>
        )
    }
}

const FriendsList = () => {
    const [open, setOpen] = useState(false);
    const [friends, setFriends] = useState([]);
    const [numFriends, setNumFriends] = useState(0);
    const [isLoading, setLoading] = useState(true);
    const profile = JSON.parse(localStorage.getItem('profile'))
    const userId = profile.result._id

    const handleClickOpen = () => {
        setOpen(true)
    }

    const handleClose = () => {
        setOpen(false)
    }

    useEffect(() => {
        axios.get(`http://localhost:5000/api/v1/users/friends/?userId=${userId}`)
            .then(res => {
                setFriends(res.data.friends)
                setNumFriends(res.data.num_friends)
                setLoading(false);
            })
    }, [])
    console.log(friends)
    console.log(numFriends)
    return(
        <Box>
            <IconButton onClick={handleClickOpen}>
                <ChildCareIcon />
            </IconButton>
            <FriendsModal open={open} handleClose={handleClose} friends={friends} numFriends={numFriends} isLoading={isLoading} />
        </Box>
    )
}

export default FriendsList
