import React, {useEffect, useState} from 'react'
import IconButton from '@mui/material/IconButton';
import Box from '@mui/material/Box';
import Dialog from '@mui/material/Dialog';
import ClickAwayListener from "@mui/material/ClickAwayListener";
import BackpackIcon from '@mui/icons-material/Backpack';
import axios from "axios";
import Card from "@mui/material/Card";
import Typography from "@mui/material/Typography";

const InventoryItem = ({itemName, itemStats}) => {
    return (
        <Card sx={{display: "flex", flexDirection: "column", textAlign: "center", border: "red solid 1" ,minWidth: 100, height: 100, m: 2}}>
            <Typography>
                {itemName} <br />
                {itemStats} <br />
            </Typography>
        </Card>
    )
}

const InventoryModal = ({open, handleClose, inventory, rendered}) => {
    if (rendered) {
        return (
            <Dialog open={open}>
                <ClickAwayListener onClickAway={handleClose}>
                    <Box sx={{p: 5}}>
                        {inventory.length === 0 ? "You have no items" : inventory.map(item => <InventoryItem itemName={item.name} itemCost={item.cost} />)}
                    </Box>
                </ClickAwayListener>
            </Dialog>
        )
    }

}

const Inventory = () => {
    const [inventory, setInventory] = useState([])
    const [rendered, setRendered] = useState(false)
    const profile = JSON.parse(localStorage.getItem('profile'))
    const userId = profile.result._id

    useEffect(() => {
        axios.get(`http://localhost:5000/api/v1/users/inventory?userId=${userId}`)
            .then(
                response => {
                    setInventory(response.data)
                    setRendered(true)
                }
            )
            .catch(error => {
                console.log(error)
            })

    }, [] )

    const [open, setOpen] = useState(false);

    const handleClickOpen = () => {
        setOpen(true)
    }

    const handleClose = () => {
        setOpen(false)
    }

    return(
        <Box>
            <IconButton onClick={handleClickOpen}>
                <BackpackIcon />
            </IconButton>
            {<InventoryModal open={open} handleClose={handleClose} inventory={inventory}/>}

        </Box>

    )
}

export default Inventory
