import React, {useEffect, useState} from 'react'
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import Typography from '@mui/material/Typography';
import Avatar from '@mui/material/Avatar';
import axios from "axios";

const CharacterDisplay = () => {
    const [ character, setCharacterState ] = useState({"_id":{"$oid":"61a44e81517f82b266e162ba"},"user_name":"new_user","password":"$2a$12$StoYLNGuvQirBN9CRzTrFedFxAz6ALWtR9ujdT4tGd7hGcZ0Bh4qy","friends_list":{"num_friends":{"$numberInt":"0"},"friends":[]},"character":{"char_name":null,"stats":{"level":{"$numberInt":"1"},"xp_to_next_level":{"$numberInt":"100"},"max_hp":{"$numberInt":"10"},"current_hp":{"$numberInt":"10"},"gold":{"$numberInt":"0"}},"inventory":[]},"tasks":[],"date_created":{"$date":{"$numberLong":"1638157952853"}}})

    const profile = JSON.parse(localStorage.getItem('profile'))
    const userId = profile.result._id
    console.log(userId, typeof(userId))
    console.log(`http://localhost:5000/api/v1/users/character?userId=${userId}`)
    useEffect(() =>{
        axios.get(`http://localhost:5000/api/v1/users/character?userId=${userId}`)
            .then(
                response => {
                    console.log(response.data)
                    const character = response.data;
                    setCharacterState(character)

                    console.log(character)

                }
            )
            .catch(error => {
                console.log(error)
            })
    },[])

    console.log(character)

    return (
            <Card>
                <CardContent sx={{display: "flex", flexDirection: "column", alignItems: "center", textAlign: "center"}}>
                    <Avatar sx={{mb: 2}}/>
                    <Typography variant="p">
                        {character.char_name}
                    </Typography>
                    <Typography style={{wordWrap: "break-word"}}>
                        {character.stats.level}
                        <br/>
                        {character.stats.xp_to_next_level}
                        <br/>
                        {character.stats.gold}
                    </Typography>
                    <Typography>
                    </Typography>
                </CardContent>
            </Card>
    )
}

export default CharacterDisplay;
