import React, {useEffect, useState} from 'react'
import IconButton from '@mui/material/IconButton';
import Box from '@mui/material/Box';
import Dialog from '@mui/material/Dialog';
import ChildCareIcon from '@mui/icons-material/ChildCare';
import ClickAwayListener from "@mui/material/ClickAwayListener";
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';

import axios from "axios";
import TextField from "@mui/material/TextField";

const AddFriend = () => {
    return (
        <TextField id="outlined-basic" label="Enter friends name" variant="outlined"  size="small" sx={{width: "100%"}} />
    )
}

const Friend = ({user}) => {
    return (
        <ListItem>
            <p>{user.name}</p>
        </ListItem>
    )
}

const FriendsListDisplay = ({friends}) => {
    return (
        <List>
            {friends.map(friend => <Friend user={friend}/>)}
        </List>
    )
}

const FriendsModal = ({open, loading, handleClose, friends, numFriends}) => {
    if (loading){
        return (
            <Dialog open={open} sx={{padding: 5}}>
                <ClickAwayListener onClickAway={handleClose}>

                    <Box sx={{p: 5}}>
                        <p>loading</p>
                    </Box>

                </ClickAwayListener>
            </Dialog>

        )
    } else {
        return (

            <Dialog open={open} sx={{padding: 5}}>
                <ClickAwayListener onClickAway={handleClose}>
                    <Box sx={{p: 5}}>
                        <p>{numFriends}</p>
                        <FriendsListDisplay friends={friends}/>
                    </Box>
                    <AddFriend />
                </ClickAwayListener>
            </Dialog>

        )
    }
}

const FriendsList = () => {
    const [open, setOpen] = useState(false);
    const [friends, setFriends] = useState([]);
    const [numFriends, setNumFriends] = useState(0);
    const [isLoading, setLoading] = useState(true);
    const profile = JSON.parse(localStorage.getItem('profile'))
    const userId = profile.result._id

    const handleClickOpen = () => {
        setOpen(true)
    }

    const handleClose = () => {
        setOpen(false)
    }

    useEffect(() => {
        axios.get(`http://localhost:5000/api/v1/users/friends/?userId=${userId}`)
            .then(res => {
                setFriends(res.data.friends)
                setNumFriends(res.data.num_friends)
                setLoading(false);
            })
    }, [])
    console.log(friends)
    console.log(numFriends)
    return(
        <Box>
            <IconButton onClick={handleClickOpen}>
                <ChildCareIcon />
            </IconButton>
            <FriendsModal open={open} handleClose={handleClose} friends={friends} numFriends={numFriends} loading={isLoading}/>
        </Box>
    )
}

export default FriendsList
