import React, {useEffect, useState} from 'react'
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import Typography from '@mui/material/Typography';
import Avatar from '@mui/material/Avatar';
import axios from "axios";

const CharacterDisplay = () => {
    const [ character, setCharacterState ] = useState({})
    const profile = JSON.parse(localStorage.getItem('profile'))
    const userId = profile.result._id

    useEffect(() =>{
        axios.get(`http://localhost:5000/api/v1/users/character?userId=${userId}`)
            .then(
                response => {
                    const resChar = response.data;
                    setCharacterState(resChar)
                    console.log("in response", character)
                }
            )
            .catch(error => {
                console.log(error)
            })
    },[])



    return (
            <Card>
                <CardContent sx={{display: "flex", flexDirection: "column", alignItems: "center", textAlign: "center"}}>
                    <Avatar sx={{mb: 2}}/>
                    <Typography variant="p">
                        {character.char_name}
                    </Typography>

                </CardContent>
            </Card>
    )
}

export default CharacterDisplay;
