import React, {useEffect, useState} from 'react'
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import Typography from '@mui/material/Typography';
import Avatar from '@mui/material/Avatar';
import Inventory from "../Menu/Inventory"
import axios from "axios";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";

const NameDisplay = ({name}) => {
    const profile = JSON.parse(localStorage.getItem('profile'))
    const userId = profile.result._id
    const [newName, setNewName] = useState("")

    // const handleQuestChange = (event) => {
    //     setNewQuest(event.target.value)
    // }

    const handleNameChange = (event) => {
        setNewName(event.target.value)
    }


    const onKeyPress = (event) => {
        if (event.keyCode === '13') {
            console.log('enter key was pressed');
        }
    }

    const handleSubmit = () => {
        const newNameObj = {
            user_id: userId,
            char_name: newName
        }
        axios.put(`http://localhost:5000/api/v1/users/character`, newNameObj)
            .then(r => console.log(r))
            .catch(e => console.log(e.response))
    }


    if (name === null){
        return (
            <TextField onSubmit={handleSubmit} label="Name" variant="outlined"  onChange={handleNameChange} size="small" sx={{width: "100%"}} />
        )
    }
    else {
        return (
            <Typography variant="p">
                {name}
            </Typography>
        )
    }
}


const CharacterDisplay = ({character}) => {
    console.log(character)
    return (
            <Card sx={{width: 150, mb: 2}}>
                <CardContent sx={{display: "flex", flexDirection: "column", alignItems: "center", textAlign: "center"}}>
                    <Avatar sx={{mb: 1}}/>
                    <Box sx={{mt: 1}} alignItems="center">
                        {character && <NameDisplay name={character.char_name} />}
                        <Typography variant="p">
                            Level: {character && character.stats.level} <br />
                            {character && character.stats.current_xp} / {character && character.stats.xp_to_next_level} <br />
                            Gold: {character && character.stats.gold}
                        </Typography>
                    </Box>

                    <Inventory />
                </CardContent>
            </Card>
    )
}

export default CharacterDisplay;
